(8017, 218, 'year_content_0_year_illustration', '42'),
(8018, 218, '_year_content_0_year_illustration', 'field_56d80df5306d2'),
(8019, 218, 'year_content_0_year_subheading', 'Hög medarbetardelaktighet'),
(8020, 218, '_year_content_0_year_subheading', 'field_56d80e33306d3'),
(8021, 218, 'year_content_0_year_text', 'APLs medarbetare är starkt engagerade i verksamhetens utveckling. I början av 2015 initierades APL Referensgrupp som ska fångar upp de samtal som dagligen pågår på våra arbetsplatser och leda till konkreta förbättringsförslag.'),
(8022, 218, '_year_content_0_year_text', 'field_56d80e75306d4'),
