(8031, 218, 'year_content_2_year_subheading', 'APL förbättrar märkningen av produkter'),
(8032, 218, '_year_content_2_year_subheading', 'field_56d80e33306d3'),
(8033, 218, 'year_content_2_year_text', 'APL har under året infört en ny märkning på lagerberedningar för att förbättra patientsäkerheten och minska risken för läkemedelsförväxlingar.'),
(8034, 218, '_year_content_2_year_text', 'field_56d80e75306d4'),
(8035, 218, 'year_content_3_year_illustration', '43'),
