(9950, 225, '_year_content_0_year_illustration', 'field_56d80df5306d2'),
(9951, 225, 'year_content_0_year_subheading', 'Hög medarbetardelaktighet'),
(9952, 225, '_year_content_0_year_subheading', 'field_56d80e33306d3'),
(9953, 225, 'year_content_0_year_text', 'APLs medarbetare är starkt engagerade i verksamhetens utveckling. I början av 2015 initierades APL Referensgrupp som ska fångar upp de samtal som dagligen pågår på våra arbetsplatser och leda till konkreta förbättringsförslag.'),
(9954, 225, '_year_content_0_year_text', 'field_56d80e75306d4'),
