(9958, 225, '_year_content_1_year_subheading', 'field_56d80e33306d3'),
(9959, 225, 'year_content_1_year_text', 'APLs medarbetare är starkt engagerade i verksamhetens utveckling. I början av 2015 initierades APL Referensgrupp som ska fångar upp de samtal som dagligen pågår på våra arbetsplatser och leda till konkreta förbättringsförslag.'),
(9960, 225, '_year_content_1_year_text', 'field_56d80e75306d4'),
(9961, 225, 'year_content_2_year_illustration', '44'),
(9962, 225, '_year_content_2_year_illustration', 'field_56d80df5306d2'),
(9963, 225, 'year_content_2_year_subheading', 'APL förbättrar märkningen av produkter'),
(9964, 225, '_year_content_2_year_subheading', 'field_56d80e33306d3'),
(9965, 225, 'year_content_2_year_text', 'APL har under året infört en ny märkning på lagerberedningar för att förbättra patientsäkerheten och minska risken för läkemedelsförväxlingar.'),
(9966, 225, '_year_content_2_year_text', 'field_56d80e75306d4'),
(9967, 225, 'year_content_3_year_illustration', '43'),
(9968, 225, '_year_content_3_year_illustration', 'field_56d80df5306d2'),
