(12098, 237, '_year_content_3_year_subheading', 'field_56d80e33306d3'),
(12099, 237, 'year_content_3_year_text', 'För att effektivisera APLs informationsflöde och hanteringen av styrande dokument har vi under året uppgraderat vår informationsplattform, SharePoint.'),
(12100, 237, '_year_content_3_year_text', 'field_56d80e75306d4'),
(12101, 237, 'year_content', '4'),
